// Extract destinations from the API response
const destinations = items[0].json.destinations || [];

// Filter out destinations without flight data
const validDestinations = destinations.filter(dest => dest.flight && dest.flight.price);

// Return as individual items for further processing
return validDestinations.map(dest => ({ json: dest }));

// Get the currency from the parameters
const params = $('Set Default Parameters').first().json;
const currency = params.currency || 'USD';
const departureId = params.departure_id || 'ZRH';
const adults = params.adults || 1;

// Helper to build Google Flights URL
function buildFlightLink(origin, destAirport, outboundDate, returnDate, numAdults) {
  const baseUrl = 'https://www.google.com/travel/flights';
  const query = `flights from ${origin} to ${destAirport} on ${outboundDate} returning ${returnDate}`;
  return `${baseUrl}?q=${encodeURIComponent(query)}&curr=${currency}&px=${numAdults}`;
}

// Helper to build Google Flights URL filtered by airline
function buildAirlineLink(origin, destAirport, outboundDate, returnDate, airlineName, numAdults) {
  const baseUrl = 'https://www.google.com/travel/flights';
  const query = `flights from ${origin} to ${destAirport} on ${outboundDate} returning ${returnDate} on ${airlineName}`;
  return `${baseUrl}?q=${encodeURIComponent(query)}&curr=${currency}&px=${numAdults}`;
}

// Format each destination simply
const cheapFlights = items.map((item, index) => {
  const dest = item.json;
  const flight = dest.flight || {};

  const stops = flight.stops === 0 ? 'Direct' : `${flight.stops} stop${flight.stops > 1 ? 's' : ''}`;
  const flightLink = buildFlightLink(departureId, flight.airport_code, dest.outbound_date, dest.return_date, adults);
  const airlineLink = flight.airline_name ? buildAirlineLink(departureId, flight.airport_code, dest.outbound_date, dest.return_date, flight.airline_name, adults) : null;

  return {
    rank: index + 1,
    destination: dest.name,
    country: dest.country,
    price: flight.price,
    price_display: `${currency} ${flight.price}`,
    airline: flight.airline_name,
    duration: flight.flight_duration,
    stops: stops,
    departure_date: dest.outbound_date,
    return_date: dest.return_date,
    hotel_per_night: dest.avg_cost_per_night ? `${currency} ${dest.avg_cost_per_night}` : null,
    airport: flight.airport_code,
    flight_link: flightLink,
    airline_link: airlineLink
  };
});

return [{ json: { cheap_flights: cheapFlights } }];

// Create a summary of cheap flights
const params = $('Set Default Parameters').first().json;
const currency = params.currency || 'USD';
const data = items[0].json;
const flights = data.cheap_flights || [];

// Build summary
const summary = {
  title: `Top ${flights.length} Cheapest Flights from ${params.departure_id}`,
  filters: {
    max_price: `${currency} ${params.max_price}`,
    travel_class: params.travel_class,
    stops: params.stops
  },
  results: flights.map(f => ({
    rank: f.rank,
    route: `${params.departure_id} -> ${f.destination} (${f.country})`,
    price: f.price_display,
    details: `${f.airline} | ${f.duration} | ${f.stops}`,
    dates: `${f.departure_date} to ${f.return_date}`,
    hotel: f.hotel_per_night ? `~${f.hotel_per_night}/night` : 'N/A',
    flight_link: f.flight_link,
    airline_link: f.airline_link
  })),
  cheapest: flights.length > 0 ? {
    destination: flights[0].destination,
    price: flights[0].price_display,
    savings_vs_average: null,
    flight_link: flights[0].flight_link,
    airline_link: flights[0].airline_link
  } : null
};

// Calculate average and savings
if (flights.length > 1) {
  const avgPrice = flights.reduce((sum, f) => sum + f.price, 0) / flights.length;
  const savings = Math.round(avgPrice - flights[0].price);
  summary.cheapest.savings_vs_average = `${currency} ${savings} below average`;
}

return [{ json: summary }];
